using System.Collections;using System.Collections.Generic;using UnityEngine;public abstract class Behavior{    private float mTimeout;    private float mTimer;    protected Entity mEntity;    public Behavior(Entity entity, float timeout)    {        mEntity = entity;        mTimeout = timeout;        mTimer = 0;    }    #region Virtual Methods    public abstract string GetDebugName();    public virtual void Start()    { }    public virtual void Stop()    { }    public virtual void OnSuspend()    { }    public virtual void OnResume()    { }		public virtual void Update()    { }    public virtual void FixedUpdate()    { }    #endregion    public void UpdateTimer(float delta)    {        mTimer += delta;    }    public bool IsTimedOut()    {        return mTimer > mTimeout;    }}