using System.Collections;using System.Collections.Generic;using UnityEngine;public class Collector : MonoBehaviour {    private void OnTriggerEnter(Collider collider)    {        GameObject gameObject = collider.gameObject;        if (gameObject.CompareTag("Pet"))        {            BehaviorComponent behaviorComponent = gameObject.GetComponent<BehaviorComponent>();            Entity entity = gameObject.GetComponent<Entity>();            if (entity != null && behaviorComponent != null)            {                behaviorComponent.PushOneShotBehavior(new BehaviorScore(entity));            }        }    }}